mapper.update:
UPDATE example
SET example_idx = #{exampleIdx}
, example_title = #{exampleTitle}
, example_ox = #{exampleOx}
WHERE example_no = #{exampleNo}

mapper.insert:
INSERT INTO example (
	question_no questionNo
	, example_idx exampleIdx
	, example_title exampleTitle
	, example_ox exampleOx
) VALUES (
	, #{questionNo}
	, #{exampleIdx}
	, #{exampleTitle}
	, #{exampleOx}
)
